<template>   <div>     <a :id="`basic-testimonials-${index}`"></a>     <div       :class="colors.background"       class="py-16 sm:py-24"     >       <div class="mx-auto max-w-7xl px-6 lg:px-8">         <div v-if="settings.pretitle || settings.title" class="mx-auto max-w-xl text-center">           <h2             v-if="settings.pretitle"             :style="{'color': settings.pretitle_color}"             class="text-lg font-semibold leading-8 tracking-tight"              v-text="settings.pretitle"           />           <p             v-if="settings.title"             :class="colors.text"             class="mt-2 text-3xl font-bold tracking-tight sm:text-4xl"             v-text="settings.title"             role="heading"           />         </div>         <div v-if="blocks.length" class="mx-auto mt-8 max-w-2xl sm:mt-10 lg:mx-0 lg:max-w-none">           <div class="mx-auto grid max-w-2xl grid-cols-1 gap-x-8 lg:mx-0 lg:max-w-none lg:grid-cols-3">              <div               v-for="(block, index) in blocks"               :key="index"               class="pt-8 sm:inline-block sm:w-full"               :data-sgpt-block="block.id"             >               <figure                 :class="[                   {'bg-gray-50': settings.theme === 'light'},                   {'bg-gray-800': settings.theme === 'dark'}                 ]"                 :style="blockBgColor"                 class="rounded-2xl p-8 text-sm leading-6"               >                 <blockquote                   v-if="block.content"                   :class="colors.text"                 >                   <div v-html="block.content" />                 </blockquote>                 <figcaption v-if="block.title || block.image?.filename" class="mt-6 flex items-center gap-x-4">                   <img v-if="block.image"                     class="h-10 w-10 rounded-full"                     :src="getResizeUrl(block.image, {width: 160, height: 160, crop: true})"                     :alt="block.image.alt ?? null"                   >                   <div v-if="block.title || block.image.meta">                     <div                       :class="colors.text"                       class="font-semibold"                       v-text="block.title ?? block.image.meta"                     />                   </div>                 </figcaption>               </figure>             </div>                        </div>         </div>       </div>     </div>   </div> </template><script> export default {   props: {     index: {       type: Number,       default: 0     },     schema: {       type: Object,       default: () => ({})     }   },    computed: {     settings() {       const data = {};        this.schema.settings?.forEach((setting) => {         data[setting.id] = setting.value;       });        return data;     },      blocks() {       const blocks = this.schema.blocks?.map((block) => {         const data = {           id: block.id,           type: block.type         };          block.settings.forEach((setting) => {           data[setting.id] = setting.value;         });          return data;       });        return blocks ?? [];     },      colors() {       return themeColors(this.settings.theme);     },      blockBgColor() {       if (this.settings.theme === 'light' || this.settings.theme === 'dark') {         return null;       }        return `background-color: rgba(0,0,0,0.1)`;     }   },    methods: {     getResizeUrl   } };  </script><style></style>